<template>
  <v-container fluid>
    <top-of-all-page>
      <template #logo-title>
        <div
          class="title text-uppercase mt-md-0 text-center"
          @click="$router.push({ name: 'home' })"
        >
          VinhTrang mens
        </div>
        <!-- <v-breadcrumbs :items="$route.meta.breadcrumb">
            <template v-slot:item="props">
              <router-link :to="{name:props.item.href}">{{props.item.text}}</router-link>
            </template>
          </v-breadcrumbs> -->
      </template>
      <!-- <template #sort>
        <v-select flat outlined :items="sortBys" v-model="sortName"></v-select>
      </template>-->
      <template #title-page>
        <div class="display-1 text-center text-uppercase font-bold">
          men's workout clothes
        </div>
      </template>
      <template #subtitle-page>
        <p class="caption text-center">
          Explore all men's VinhTrang clothing. Gym and fitness clothes designed
          to complement the hard work and dedication you put in your workouts.
        </p>
      </template>
    </top-of-all-page>
    <v-pagination
      v-model="page"
      v-if="products && products.meta"
      :length="products.meta.last_page"
      :total-visible="7"
      class='mb-10'
    ></v-pagination>
    <v-layout row wrap>
      <home-side-bar></home-side-bar>
      <v-flex sm6 md9>
        <v-layout wrap row v-if="products.data && products.data.length > 0">
          <v-flex
            class="pa-3 pt-0"
            sm6
            md4
            v-for="(prod, k) in products.data"
            :key="k"
          >
            <v-card flat class="ma-2 mr-2">
              <v-hover v-slot:default="{ hover }" open-delay="200">
                <v-row style="position:relative">
                  <v-img
                    @click="
                      $router.push({
                        path: `/collection/${$route.params.type}/products/${prod.slug}-${prod.color[0].name}-${prod.id}`,
                      })
                    "
                    :src="
                      prod.color.length &&
                      prod.color[0].pictures.length &&
                      prod.color[0].pictures[0].src.includes('//')
                        ? 'http' + prod.color[0].pictures[0].src
                        : prod.color.length && prod.color[0].pictures.length
                        ? prod.imgLink +
                          '/colors/' +
                          prod.color[0].id +
                          '/' +
                          prod.color[0].pictures[0].type +
                          '/' +
                          prod.color[0].pictures[0].src
                        : 'data:image/png;base64,'
                    "
                    class="dark--text align-end"
                    style="cursor:poiter"
                  >
                    <span class="newest pa-2" v-if="prod.newest">Newest</span>
                    <span class="discount pa-2" v-if="prod.discount_available"
                      >-{{ prod.discount }}%</span
                    >
                  </v-img>

                  <div
                    :style="
                      hover
                        ? 'width:100%;position:absolute;bottom:0;left:50%;background:white;text-align:center;transform:translateX(-50%); color:black'
                        : 'display:none'
                    "
                  >
                    <div class="title">Quick Add</div>
                    <v-row class="justify-center">
                      <v-btn
                        outlined
                        link
                        class="ma-1"
                        @click="add2Cart(prod, s)"
                        v-for="s in prod.color[0].sizes"
                        :key="s.id"
                        >{{ s.name }}</v-btn
                      >
                    </v-row>
                  </div>
                  <!-- </v-img> -->
                </v-row>
              </v-hover>
              <div class="text-center mt-5">
                <p
                  @click="
                    $router.push({
                      path: `/collection/${$route.params.type}/products/${prod.slug}-${prod.color[0].name}-${prod.id}`,
                    })
                  "
                  v-html="prod.product_name"
                ></p>
                <p v-html="prod.color[0].name"></p>
                <p>
                  ${{ prod.product_price }} USD
                  <span v-if="prod.discount_available">
                    -> ${{
                      prod.product_price * ((100 - prod.discount) / 100)
                    }}
                    USD</span
                  >
                </p>
              </div>
            </v-card>
          </v-flex>
        </v-layout>
        <v-layout v-else>
          <h1>Do not have any product got any products.</h1>
        </v-layout>
      </v-flex>
    </v-layout>
    <div class="text-center">
      <v-pagination
        v-model="page"
        v-if="products && products.meta"
        :length="products.meta.last_page"
        :total-visible="7"
      ></v-pagination>
    </div>
  </v-container>
</template>

<script>
import homeProductStore from "@/store/modules/homeProductStore";
export default {
  data() {
    return {
      categories: [],
      page: 1,
      filterDrawer: false,
      sortDrawer: false,
      size: null,
      key: null,
    };
  },
  watch: {
    $route: function() {
      let { category_name, type } = this.$route.params;
      this.size = null;
      this.key = null;
      this.fetchData({
        length: 50,
        category_name,
        type,
        page: this.page,
        key: this.$route.query.key || this.key,
        size_name: this.size,
      });
    },
    page: function(val) {
      let { category_name, type } = this.$route.params;
      this.fetchData({
        length: 50,
        category_name,
        type,
        page: val,
        key: this.key,
        size_name: this.size,
      });
    },
  },
  created() {
    this.$root.$on("filterKey", (val) => (this.key = val));
    this.$root.$on("filterSize", (val) => {
      this.size = val;
    });
  },
  mounted() {
    let { category_name, type } = this.$route.params;
    homeProductStore.fetchData({
      length: 50,
      category_name,
      type,
      page: this.page,
      key: this.key,
      size_name: this.size,
    });
  },
  computed: {
    products() {
      return homeProductStore.products;
    },
    fetching() {
      return homeProductStore.fetching;
    }
  },
  methods: {
    add2Cart(item, size) {
      let cart = [];
      if (localStorage.cart) {
        cart = JSON.parse(localStorage.cart);
        let index = cart.findIndex(
          (c) =>
            c.id == item.id &&
            c.color.name == item.color[0].name &&
            c.color.sizes.name == size.name
        );
        if (index !== -1) {
          cart[index].color.sizes.quantity++;
          localStorage.setItem("cart", JSON.stringify(cart));
        } else {
          let product = {
            ...item,
            product_price: item.discount_available
              ? item.product_price * ((100 - item.discount) / 100)
              : item.product_price,
            color: {
              ...item.color[0],
              sizes: {
                ...size,
                quantity: 1,
              },
            },
          };
          cart.push(product);
          localStorage.setItem("cart", JSON.stringify(cart));
        }
        this.$root.$emit("changeCartDrawer", true);
      } else {
        cart.push({
          ...item,
          product_price: item.discount_available
            ? item.product_price * ((100 - item.discount) / 100)
            : item.product_price,
          color: {
            ...item.color[0],
            sizes: {
              ...size,
              quantity: 1,
            },
          },
        });
        localStorage.setItem("cart", JSON.stringify(cart));
        this.$root.$emit("changeCartDrawer", true);
      }
    },
  },
};
</script>

<style scoped>
.newest {
  background-color: rgb(255, 0, 0);
  color: white;
  font-weight: bold;
  top: 0;
  left: 0;
  position: absolute;
}
.discount {
  background-color: rgb(255, 0, 0);
  color: white;
  font-weight: bold;
  top: 0;
  right: 0;
  position: absolute;
}
</style>
